--
-- Структура таблицы `services`
--

CREATE TABLE `services` (
  `id` int(11) NOT NULL,
  `service` varchar(250) NOT NULL,
  `price` text NOT NULL,
  `duration` text NOT NULL,
  `description` text NOT NULL,
  `image` text NOT NULL,
  `sub_headline` text DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `services`
--

INSERT INTO `services` (`id`, `service`, `price`, `duration`, `description`, `image`, `sub_headline`) VALUES
(1, 'Семейное консультирование и психотерапия ', ' цена договорная', '1 час', 'Любовь, доверие, доброта, верность, взаимопонимание, уважение.\r\nЖивут ли они в вашей семье? Счастливая семья, как минимум, обладает таким набором ценностей.В любви другого человека мы расцветаем и греемся, дарим свое тепло и радость. Доверяем друг другу и поддерживаем в разных жизненных ситуациях добрым словом. Верность помогает сохранить наш союз на долгие годы, а взаимопонимание растет с прожитыми рядом с избранником годами.Со временем мы узнаем все больше о Другом, к его особенностям относимся с уважением и пониманием. И всегда помним, что и нас принимают целостно, без деления на \"хорошесть\" и \"плохость\". А это многого стоит, стоит компромиссов и взаимных уступок, И вознаграждается такая мудрость счастливой гаванью под названием СЕМЬЯ.\r\n\r\nГотова предоставить Вашей семье профессиональную психологическую помощь и поддержку в кризисные периоды.\r\n Работаю в направлениях: Арт-терапия, Гештальт-терапия, Системная семейная терапия, Телесно-ориентированная терапия, Экзистенциальная психотерапия, Эмоционально-образная терапия.\r\nДень рождения: 15 августа 1975 г.\r\nСемейное положение: замужем, мама троих  замечательных детей.', 'img/services/48299п.jpg', ''),
(2, 'Индивидуальное консультирование и психотерапия', ' цена договорная', '1 час', 'Готова оказать Вам профессиональную психологическую помощь и поддержку в бережной атмосфере консультаций и психотерапии в работе с такими запросами:  самооценка; самореализация; самоуважение;  отношения в паре; отношения с родителями; с детьми;  семейные и личные кризисы; созависимость и зависимое поведение; одиночество и создание отношений;  ревность; измена; обида; проблемы в сексуальной сфере; тревога; страхи; депрессия;  психосоматика.', 'img/services/45478ли.jpg', ''),
(6, 'Услуги детского психолога', ' цена договорная', '1 час', 'Оптимизация детско-родительских отношений, работа с агрессивностью, тревожностью, страхами. ', 'img/services/97339пппп.jpg', ''),
(9, 'Песочная терапия', ' цена договорная', '1 час', 'Песочная терапия - это самый органичный для ребёнка способ выразить свои переживания, исследовать мир.  Картина на песке - трехмерное изображение душевного состояния. Неосознанная проблема разыгрывается в песочнице, конфликт переносится из внутреннего мира во внешний, благодаря чему становится возможным проработать психотравмирующую ситуацию на символическом уровне, а также отреагировать негативный эмоциональный опыт в процессе творческого самовыражения.', 'img/services/28924ак.jpg', ''),
(7, 'Тренинг \"Профилактика профессионального выгорания\" ', ' цена договорная', '3 часа', 'Цель: \r\n-Формирование умений и навыков по сохранению и укреплению психического здоровья сотрудников через овладение ими способами психической саморегуляции и активизацию личностных ресурсов.\r\n-Сохранение психического здоровья сотрудников.\r\nЗадачи: \r\n-Знакомство с понятием профессиональное выгорание, с его характеристиками.\r\n-Анализ проявления признаков ПВ, выделение источников неудовлетворения профессиональной деятельностью. \r\n-Психодиагностика.\r\n-Снятие эмоционального напряжения освоением способов саморегуляции эмоционального состояния.\r\n-Активизации личностных ресурсных состояний;\r\nЧисло участников тренинга может быть различным от 8 до 30 человек. \r\n', 'img/services/88927ссс.jpg', ''),
(8, 'Человек Творческий', ' цена договорная', '1 час', '«Творчество заразительно. Передай другому!» – Альберт Эйнштейн.\r\nАрт терапия для души!\r\nПриглашаю на консультации по «раскопке» творческих способностей, исследованию своего творческого потенциала. ', 'img/services/68629т.jpg', ''),
(12, 'Тренинг “Конфликты и пути их разрешения”', ' цена договорная', '3 часа', 'Провожу тренинги по наработке конструктивных навыков разрешения конфликтов.\r\nЦели: \r\nспособствовать формированию навыков конструктивного решения конфликтов;\r\nформирование  представления о конфликте.\r\nЗадачи:\r\nПоказать альтернативные варианты поведения в конфликте;\r\nпроанализировать конфликт с точки зрения положительного и отрицательного воздействия на межличностные отношения.\r\nКоличество участников от 8 до 30 человек.\r\n', 'img/services/44240images(1).jpg', '');

